import React from 'react';
import '../style/Dashboard.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import PageNavbar from './PageNavbar';
import Query2Row from './Query2Row';
import DashboardMovieRow from './DashboardMovieRow';

export default class Home extends React.Component {
  constructor(props) {
    super(props);

    // The state maintained by this React Component. This component maintains the list of genres,
    // and a list of movies for a specified genre.
    this.state = {
      buttonList: [],
      query2: [],
      movies: []
    }
    this.showQuery2 = this.showQuery2.bind(this);
    this.showMovies = this.showMovies.bind(this);
  }

  // React function that is called when the page load.
  componentDidMount() {
    // Send an HTTP request to the server.
    fetch("http://localhost:8081/genres",
    {
      method: 'GET' // The type of HTTP request.
    }).then(res => {
      // Convert the response data to a JSON.
      return res.json();
    }, err => {
      // Print the error if there is one.
      console.log(err);
    }).then(genreList => {
      if (!genreList) return;
      // Map each genreObj in genreList to an HTML element:
      // A button which triggers the showMovies function for each genre.
      let query2Divs = genreList.map((query2_Obj, i) =>
      <Query2Row state={query2_Obj.state} population={query2_Obj.population} peaceful={query2_Obj.peaceful} non_peaceful={query2_Obj.non_peaceful} peaceful_per_100K={query2_Obj.peaceful_per_100K} non_peaceful_per_100K={query2_Obj.non_peaceful_per_100K}/>
      );



      // Set the state of the genres list to the value returned by the HTTP response from the server.
      this.setState({
        query2: query2Divs
      });
    }, err => {
      // Print the error if there is one.
      console.log(err);
    });
  }

  showQuery2() {
    fetch("http://localhost:8081/genres",
    {
      method: 'GET' // The type of HTTP request.
    }).then(res => {
      // Convert the response data to a JSON.
      return res.json();
    }, err => {
      // Print the error if there is one.
      console.log(err);
    }).then(genreList => {
      if (!genreList) return;
      // Map each genreObj in genreList to an HTML element:
      // A button which triggers the showMovies function for each genre.
      let query2Divs = genreList.map((query2_Obj, i) =>
      <Query2Row state={query2_Obj.state} population={query2_Obj.population} peaceful={query2_Obj.peaceful} non_peaceful={query2_Obj.non_peaceful} peaceful_per_100K={query2_Obj.peaceful_per_100K} non_peaceful_per_100K={query2_Obj.non_peaceful_per_100K}/>
      );



      // Set the state of the genres list to the value returned by the HTTP response from the server.
      this.setState({
        query2: query2Divs
      });
    }, err => {
      // Print the error if there is one.
      console.log(err);
    });
  }
  /* ---- Q1b (Dashboard) ---- */
  /* Set this.state.movies to a list of <DashboardMovieRow />'s. */
  showMovies(genre) {
    fetch(`http://localhost:8081/genres/${genre}`,
    {
      method: 'GET' // The type of HTTP request.
    }).then(res => {
      // Convert the response data to a JSON.
      return res.json();
    }, err => {
      // Print the error if there is one.
      console.log(err);
    }).then(movieList => {
      if (!movieList) return;

      let movieRows = movieList.map((movieObj, i) =>
      <DashboardMovieRow title={movieObj.title} rating={movieObj.rating} votes={movieObj.vote_count} />
      );

      this.setState({
        movies: movieRows
      });
    }, err => {
      // Print the error if there is one.
      console.log(err);
    });
  }

  render() {    
    return (
      <div className="Dashboard">

        <PageNavbar active="Home" />
        <div class = "text-center">
                <h3 class="display-3 font-weight-bold text-white">Protest Data in the United States in 2020</h3>
            </div>

        <div className="container movies-container">
          <br></br>
          <div className="jumbotron">Number of Peaceful and Violent Protests in each State in 2020
            <div className="movies-container">
              <div className="movies-header"> 
                <div className="header-lg"><strong>State</strong></div>
                <div className="header"><strong>Population</strong></div>
                <div className="header"><strong># Peaceful Protests</strong></div>
                <div className="header"><strong># Violent Protests</strong></div>
                <div className="header"><strong># Peaceful per 100K</strong></div>
                <div className="header"><strong># Violent per 100K</strong></div>
              </div>
              <div className="results-container" id="results">
                {this.state.query2}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
