import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../style/Dashboard.css';
import PageNavbar from './PageNavbar';
import Query2Row from './Query2Row';
import Query3Row from './Query3Row';

export default class Counties extends React.Component {
  constructor(props) {
    super(props);

    // The state maintained by this React Component. This component maintains the list of genres,
    // and a list of movies for a specified genre.
    this.state = {
      query: [],
      states: [],
      selectedState: ""
    }
    this.showQuery = this.showQuery.bind(this);
  }

  // React function that is called when the page load.
  componentDidMount() {

    fetch("http://localhost:8081/states/query5b",  //change to appropriate URL based on index.js in server folder
    {
      method: 'GET' // The type of HTTP request.
    }).then(res => {
      // Convert the response data to a JSON.
      return res.json();
    }, err => {
      // Print the error if there is one.
      console.log(err);
    }).then(stateList => {
      if (!stateList) return;
      let stateDivs = stateList.map((state_Obj, i) => //
      <option value={state_Obj.state === 'DISTRICT OF COLUMBIA' ? 'D.C.' : state_Obj.state}>{state_Obj.state === 'DISTRICT OF COLUMBIA' ? 'D.C.' : state_Obj.state}</option>
      );
      //
      this.setState({
        states: stateDivs
      });
    }, err => {
      // Print the error if there is one.
      console.log(err);
    });


  }

  handleChange(e) {
    console.log(e.target.value);
    this.setState({
      selectedState: e.target.value
    });
  }

  showQuery() {
    fetch("http://localhost:8081/counties/query",  //change to appropriate URL based on index.js in server folder
    {
      method: 'GET' // The type of HTTP request.
    }).then(res => {
      // Convert the response data to a JSON.
      return res.json();
    }, err => {
      // Print the error if there is one.
      console.log(err);
    }).then(queryList => {
      if (!queryList) return;
      let query2Divs = queryList.map((query2_Obj, i) =>
      <Query2Row state={query2_Obj.state} population={query2_Obj.population} peaceful={query2_Obj.peaceful} non_peaceful={query2_Obj.non_peaceful} peaceful_per_100K={query2_Obj.peaceful_per_100K} non_peaceful_per_100K={query2_Obj.non_peaceful_per_100K}/>
      );

      // Set the appropriate state variable to the value returned by the HTTP response from the server.
      this.setState({
        query: query2Divs
      });
    }, err => {
      // Print the error if there is one.
      console.log(err);
    });
  }


  render() {    
    return (
      <div className="Counties">

        <PageNavbar active="Counties" />
        <div class = "text-center">
          <h3 class="display-3 font-weight-bold text-white">County Comparison</h3>
        </div>

        <div className="container query-container">
          <div className="dropdown-container">
            <select value={this.state.selectedState} onChange={this.handleChange} className="dropdown" id="statesDropdown">
              <option select value> -- Select a State -- </option>
              {this.state.states}
            </select>
            <button className="submit-btn" id="stateSubmitBtn" onClick={this.submitState}>Submit</button>
          </div>
          <br></br>
          //TAKE OUT THE INVISIBLE FROM THE CLASSNAME TO MAKE IT VISIBLE AGAIN; ALSO TAKE OUT THIS LINE
          <div className="jumbotron invisible">
            {this.state.tableTitle}
            <div className="query-container">
              {this.state.tableHeader}
              <div className="results-container" id="results">
                {this.state.query}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
