    //         });
    //     }
    // }
    render() {
        console.log(this.props.chartData);
    return (
        <div>
        <h2>Number of Events</h2>
            <Line ref="chart2" data={data} />
        </div>
    );
    }

    componentDidMount() {
        data.datasets[0].data = this.props.chartData
        const { datasets } = this.refs.chart.chartInstance.data
        console.log(datasets[0].data);
    }
}
