    }, err => {
      // Print the error if there is one.
      console.log(err);
    }).then(genreList => {
      if (!genreList) return;
      // Map each genreObj in genreList to an HTML element:
      // A button which triggers the showMovies function for each genre.
      let query2Divs = genreList.map((query2_Obj, i) =>
      <Query2Row state={query2_Obj.state} population={query2_Obj.population} peaceful={query2_Obj.peaceful} non_peaceful={query2_Obj.non_peaceful} peaceful_per_100K={query2_Obj.peaceful_per_100K} non_peaceful_per_100K={query2_Obj.non_peaceful_per_100K}/>
      );


      // Set the state of the genres list to the value returned by the HTTP response from the server.
      this.setState({
        query2: query2Divs
      });
    }, err => {
      // Print the error if there is one.
      console.log(err);
    });
    */
  }

  showQuery2() {
    fetch("http://localhost:8081/genres/query2",
    {
      method: 'GET' // The type of HTTP request.
    }).then(res => {
      // Convert the response data to a JSON.
      return res.json();
    }, err => {
      // Print the error if there is one.
      console.log(err);
    }).then(genreList => {
      if (!genreList) return;
      // Map each genreObj in genreList to an HTML element:
      // A button which triggers the showMovies function for each genre.
      let query2Divs = genreList.map((query2_Obj, i) =>
      <Query2Row state={query2_Obj.state} population={query2_Obj.population} peaceful={query2_Obj.peaceful} non_peaceful={query2_Obj.non_peaceful} peaceful_per_100K={query2_Obj.peaceful_per_100K} non_peaceful_per_100K={query2_Obj.non_peaceful_per_100K}/>
      );

      let header = [
        <div className="movies-header"> 
          <div className="header"><strong>State</strong></div>
          <div className="header"><strong>Population</strong></div>
          <div className="header"><strong># Peaceful Protests</strong></div>
          <div className="header"><strong># Violent Protests</strong></div>
          <div className="header"><strong># Peaceful per 100K</strong></div>
          <div className="header"><strong># Violent per 100K</strong></div>
        </div>
      ];

      let title = [
        <div className="text-center display-5 font-weight-bold">Number of Peaceful and Violent Protests in each State in 2020</div>
      ];


      // Set the state of the genres list to the value returned by the HTTP response from the server.
      this.setState({
        queryResults: query2Divs,
        tableTitle: title,
        tableHeader: header,
        showJumbo: true
      });
    }, err => {
      // Print the error if there is one.
      console.log(err);
    });
  }

  showQuery3() {
    fetch("http://localhost:8081/genres/query3",
    {
      method: 'GET' // The type of HTTP request.
    }).then(res => {
      // Convert the response data to a JSON.
      return res.json();
    }, err => {
      // Print the error if there is one.
      console.log(err);
    }).then(query3List => {
      if (!query3List) return;
      // Map each genreObj in genreList to an HTML element:
      // A button which triggers the showMovies function for each genre.
      let query3Divs = query3List.map((query3_Obj, i) =>
      <Query3Row event={query3_Obj.event_type} sub_event={query3_Obj.sub_event_type} count={query3_Obj.count}/>
      );

      let title = [
        <div className="text-center display-5 font-weight-bold">Types and Count of Different Protests throughout the US</div>
      ];
      let header = [
        <div className="movies-header"> 
          <div className="header"><strong>Event Type</strong></div>
          <div className="header"><strong>Subtype</strong></div>
          <div className="header"><strong>Amount</strong></div>
        </div>
      ];


      // Set the state of the genres list to the value returned by the HTTP response from the server.
      this.setState({
        queryResults: query3Divs,
        tableTitle: title,
        tableHeader: header,
        showJumbo: true
      });
    }, err => {
      // Print the error if there is one.
      console.log(err);
    });
  }

  render() {
    let isPressed = this.state.showJumbo;
    return (
      <div className="Dashboard">

        <PageNavbar active="Home" />
        <div class = "text-center">
          <h3 class="display-3 font-weight-bold text-white">Protest Data in the United States in 2020</h3>
        </div>
        <br></br>
        <br></br>

        <div className="container movies-container">

            <div className="buttons-container">
              {this.state.buttonList}
            </div>

          <br></br>
          { isPressed ?
          <div className="jumbotron">
            {this.state.tableTitle}
            <div className="movies-container">
              {this.state.tableHeader}
              <div className="results-container" id="results">
                {this.state.queryResults}
              </div>
            </div>
          </div>
          :
          null
          }
          <img src='./us-map.png' class="rounded mx-auto d-block" alt="States with Protests during 2020"></img>
          <br></br>
          <br></br>
        </div>
      </div>
    );
  }
}

